// frontend/src/pages/AdminInput.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function AdminInput() {
  // Single‐item form state
  const [form, setForm] = useState({
    supplier: '',
    product_code: '',
    variant: '',
    cost: '',
    remark: '',
    proposed_selling_price: '',
  });
  const [imageFile, setImageFile] = useState(null);

  // Bulk‐upload file
  const [bulkFile, setBulkFile] = useState(null);

  // List of products & edit mode
  const [products, setProducts] = useState([]);
  const [editId, setEditId] = useState(null);

  // Status message
  const [message, setMessage] = useState('');

  // Fetch all products
  const loadProducts = async () => {
    try {
      const res = await axios.get('http://127.0.0.1:5000/api/user/products');
      setProducts(res.data);
    } catch (err) {
      console.error(err);
      setMessage('Failed to load products');
    }
  };

  useEffect(() => {
    loadProducts();
  }, []);

  // Handlers for single‐item form
  const handleChange = e =>
    setForm({ ...form, [e.target.name]: e.target.value });
  const handleImage = e => setImageFile(e.target.files[0] || null);

  const resetForm = () => {
    setForm({
      supplier: '',
      product_code: '',
      variant: '',
      cost: '',
      remark: '',
      proposed_selling_price: '',
    });
    setImageFile(null);
    setEditId(null);
    setMessage('');
  };

  const onSubmit = async e => {
    e.preventDefault();
    const data = new FormData();
    Object.entries(form).forEach(([k, v]) => data.append(k, v));
    if (imageFile) data.append('image', imageFile);

    try {
      let res;
      if (editId) {
        res = await axios.put(
          `http://127.0.0.1:5000/api/admin/products/${editId}`,
          data
        );
        setMessage(`Updated id=${res.data.id}`);
      } else {
        res = await axios.post(
          'http://127.0.0.1:5000/api/admin/products',
          data
        );
        setMessage(`Created id=${res.data.id}`);
      }
      resetForm();
      loadProducts();
    } catch (err) {
      console.error(err);
      setMessage(`Error: ${err.response?.data?.error || err.message}`);
    }
  };

  // Handlers for edit/delete actions
  const onEdit = p => {
    setEditId(p.id);
    setForm({
      supplier: p.supplier,
      product_code: p.product_code,
      variant: p.variant || '',
      cost: p.cost,
      remark: p.remark || '',
      proposed_selling_price: p.proposed_selling_price,
    });
    setImageFile(null);
    window.scrollTo(0, 0);
  };

  const onDelete = async id => {
    if (!window.confirm('Delete this product?')) return;
    try {
      await axios.delete(`http://127.0.0.1:5000/api/admin/products/${id}`);
      setMessage(`Deleted id=${id}`);
      if (editId === id) resetForm();
      loadProducts();
    } catch (err) {
      console.error(err);
      setMessage('Delete failed');
    }
  };

  // Handler for bulk‐upload form
  const onBulkUpload = async e => {
    e.preventDefault();
    if (!bulkFile) {
      setMessage('Please select an Excel file first');
      return;
    }
    const data = new FormData();
    data.append('file', bulkFile);

    try {
      const res = await axios.post(
        'http://127.0.0.1:5000/api/admin/products/bulk-upload',
        data
      );
      setMessage(
        `Bulk: created ${res.data.created}, updated ${res.data.updated}`
      );
      setBulkFile(null);
      loadProducts();
    } catch (err) {
      console.error(err);
      setMessage(`Bulk upload error: ${err.response?.data?.error || err.message}`);
    }
  };

  return (
    <div style={{ maxWidth: 800, margin: '1em auto' }}>
      {/* Single‐item Add/Edit form */}
      <h2>{editId ? 'Edit' : 'Add'} Product</h2>
      <form onSubmit={onSubmit} style={{ display: 'grid', gap: 8 }}>
        {[
          ['supplier', 'Supplier'],
          ['product_code', 'Product Code'],
          ['variant', 'Variant'],
          ['cost', 'Cost', 'number'],
          ['proposed_selling_price', 'Proposed Price', 'number'],
        ].map(([name, placeholder, type]) => (
          <input
            key={name}
            name={name}
            placeholder={placeholder}
            type={type || 'text'}
            value={form[name]}
            onChange={handleChange}
            required={['supplier', 'product_code', 'cost'].includes(name)}
          />
        ))}
        <textarea
          name="remark"
          placeholder="Remark"
          value={form.remark}
          onChange={handleChange}
        />
        <label>
          Image:{' '}
          <input type="file" accept="image/*" onChange={handleImage} />
        </label>
        <div>
          <button type="submit">{editId ? 'Update' : 'Save'}</button>
          {editId && (
            <button
              type="button"
              onClick={resetForm}
              style={{ marginLeft: 8 }}
            >
              Cancel
            </button>
          )}
        </div>
      </form>

      {/* Bulk‐upload form */}
      <section style={{ margin: '2em 0' }}>
        <h3>Bulk Upload</h3>
        <form
          onSubmit={onBulkUpload}
          style={{ display: 'flex', gap: 8, alignItems: 'center' }}
        >
          <input
            type="file"
            accept=".xlsx"
            onChange={e => setBulkFile(e.target.files[0])}
          />
          <button type="submit">Upload Excel</button>
        </form>
      </section>

      {/* Status message */}
      {message && <p style={{ marginTop: '1em' }}>{message}</p>}

      <hr style={{ margin: '2em 0' }} />

      {/* Products table */}
      <h2>All Products</h2>
      <table
        border="1"
        cellPadding="6"
        style={{ width: '100%', borderCollapse: 'collapse' }}
      >
        <thead>
          <tr>
            {[
              'ID',
              'Supplier',
              'Code',
              'Variant',
              'Cost',
              'Updated',
              'Remark',
              'Proposed',
              'Image',
              'Actions',
            ].map(h => (
              <th key={h}>{h}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.supplier}</td>
              <td>{p.product_code}</td>
              <td>{p.variant}</td>
              <td>{p.cost}</td>
              <td>{new Date(p.date_updated).toLocaleString()}</td>
              <td>{p.remark}</td>
              <td>{p.proposed_selling_price}</td>
              <td style={{ textAlign: 'center' }}>
                {p.image_url ? (
                  <a
                    href={p.image_url}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <img
                      src={p.image_url}
                      alt=""
                      style={{
                        width: 40,
                        height: 40,
                        objectFit: 'cover',
                      }}
                    />
                  </a>
                ) : (
                  '—'
                )}
              </td>
              <td>
                <button onClick={() => onEdit(p)}>Edit</button>
                <button
                  onClick={() => onDelete(p.id)}
                  style={{ marginLeft: 4 }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
