// frontend/src/pages/AdminInput.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function AdminInput() {
  // Form state
  const [form, setForm] = useState({
    supplier: '',
    product_code: '',
    variant: '',
    cost: '',
    remark: '',
    proposed_selling_price: '',
  });
  const [imageFile, setImageFile] = useState(null);
  const [message, setMessage] = useState('');

  // All products
  const [products, setProducts] = useState([]);

  // Load products from API
  const loadProducts = async () => {
    try {
      const res = await axios.get('http://127.0.0.1:5000/api/user/products');
      setProducts(res.data);
    } catch (err) {
      console.error('Failed loading products', err);
    }
  };

  useEffect(() => {
    loadProducts();
  }, []);

  // Handle form inputs
  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };
  const handleImage = e => {
    setImageFile(e.target.files[0]);
  };

  // Submit single product
  const submitSingle = async e => {
    e.preventDefault();

    const data = new FormData();
    Object.entries(form).forEach(([k, v]) => data.append(k, v));
    if (imageFile) data.append('image', imageFile);

    try {
      const res = await axios.post(
        'http://127.0.0.1:5000/api/admin/products',
        data
      );
      setMessage(`Saved id=${res.data.id}`);
      setForm({
        supplier: '',
        product_code: '',
        variant: '',
        cost: '',
        remark: '',
        proposed_selling_price: '',
      });
      setImageFile(null);
      loadProducts();
    } catch (err) {
      setMessage(`Error: ${err.response?.data?.error || err.message}`);
    }
  };

  return (
    <div style={{ maxWidth: 800, margin: '0 auto' }}>
      <h2>Add / Update Product</h2>
      <form onSubmit={submitSingle} style={{ display: 'grid', gap: 8 }}>
        {[
          ['supplier', 'Supplier'],
          ['product_code', 'Product Code'],
          ['variant', 'Variant'],
          ['cost', 'Cost', 'number'],
          ['proposed_selling_price', 'Proposed Price', 'number'],
        ].map(([name, placeholder, type]) => (
          <input
            key={name}
            name={name}
            placeholder={placeholder}
            type={type || 'text'}
            value={form[name]}
            onChange={handleChange}
            required={
              name === 'supplier' ||
              name === 'product_code' ||
              name === 'cost'
            }
          />
        ))}
        <textarea
          name="remark"
          placeholder="Remark"
          value={form.remark}
          onChange={handleChange}
        />
        <label>
          Image:
          <input type="file" accept="image/*" onChange={handleImage} />
        </label>
        <button type="submit">Save</button>
      </form>

      {message && <p style={{ marginTop: 8 }}>{message}</p>}

      <hr style={{ margin: '24px 0' }} />

      <h2>All Products</h2>
      <table
        border="1"
        cellPadding="4"
        style={{ width: '100%', marginTop: 8, borderCollapse: 'collapse' }}
      >
        <thead>
          <tr>
            {[
              'ID',
              'Supplier',
              'Code',
              'Variant',
              'Cost',
              'Updated',
              'Remark',
              'Proposed',
              'Image',
            ].map(h => (
              <th key={h} style={{ background: '#f0f0f0' }}>
                {h}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.supplier}</td>
              <td>{p.product_code}</td>
              <td>{p.variant}</td>
              <td>{p.cost}</td>
              <td>{new Date(p.date_updated).toLocaleString()}</td>
              <td>{p.remark}</td>
              <td>{p.proposed_selling_price}</td>
              <td style={{ textAlign: 'center' }}>
                {p.image_url ? (
                  <a href={p.image_url} target="_blank" rel="noopener noreferrer">
                      <img
                        src={p.image_url}
                        alt={`img-${p.id}`}
                        style={{ width:40, height:40, objectFit:'cover', cursor:'pointer' }}
                      />
                    </a>
                  ) : 'â€”'}
                </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
