// frontend/src/pages/AdminInput.jsx

import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function AdminInput({ role }) {
  const isAdmin = role === 'admin';

  // ─── Search Filters ─────────────────────────────────────────────────────────
  const [filters, setFilters] = useState({
    supplier: '',
    product_code: '',
    variant: '',
  });

  // ─── Form / Bulk / Products State ────────────────────────────────────────────
  const [form, setForm] = useState({
    supplier: '',
    product_code: '',
    variant: '',
    cost: '',
    remark: '',
    proposed_selling_price: '',
  });
  const [imageFiles, setImageFiles] = useState([]);
  const [bulkFile, setBulkFile] = useState(null);
  const [products, setProducts] = useState([]);
  const [editId, setEditId] = useState(null);
  const [message, setMessage] = useState('');

  // ─── Load Products (with optional filters) ───────────────────────────────────
  const loadProducts = async (qry = {}) => {
    try {
      const res = await axios.get(
        'http://127.0.0.1:5000/api/user/products',
        { params: qry }
      );
      setProducts(res.data);
    } catch {
      setMessage('Failed to load products');
    }
  };

  // Initial load
  useEffect(() => {
    loadProducts(filters);
  }, []);

  // ─── Handlers ────────────────────────────────────────────────────────────────
  const handleChange = e =>
    setForm({ ...form, [e.target.name]: e.target.value });
  const handleImages = e => setImageFiles(Array.from(e.target.files) || []);

  const resetForm = () => {
    setForm({
      supplier: '',
      product_code: '',
      variant: '',
      cost: '',
      remark: '',
      proposed_selling_price: '',
    });
    setImageFiles([]);
    setEditId(null);
    setMessage('');
  };

  const onSubmit = async e => {
    e.preventDefault();
    const data = new FormData();
    Object.entries(form).forEach(([k, v]) => data.append(k, v));
    imageFiles.forEach(f => data.append('images', f));

    try {
      const url = editId
        ? `http://127.0.0.1:5000/api/admin/products/${editId}`
        : 'http://127.0.0.1:5000/api/admin/products';
      const method = editId ? 'put' : 'post';
      const res = await axios[method](url, data);
      setMessage(`${editId ? 'Updated' : 'Created'} id=${res.data.id}`);
      resetForm();
      loadProducts(filters);
    } catch (err) {
      setMessage(`Error: ${err.response?.data?.error || err.message}`);
    }
  };

  const onEdit = p => {
    setEditId(p.id);
    setForm({
      supplier: p.supplier,
      product_code: p.product_code,
      variant: p.variant || '',
      cost: p.cost,
      remark: p.remark || '',
      proposed_selling_price: p.proposed_selling_price,
    });
    setImageFiles([]);
    window.scrollTo(0, 0);
  };

  const onDelete = async id => {
    if (!window.confirm('Delete this product?')) return;
    try {
      await axios.delete(`http://127.0.0.1:5000/api/admin/products/${id}`);
      setMessage(`Deleted id=${id}`);
      if (editId === id) resetForm();
      loadProducts(filters);
    } catch {
      setMessage('Delete failed');
    }
  };

  const onBulkUpload = async e => {
    e.preventDefault();
    if (!bulkFile) return setMessage('Select an Excel file first');
    const data = new FormData();
    data.append('file', bulkFile);
    try {
      const res = await axios.post(
        'http://127.0.0.1:5000/api/admin/products/bulk-upload',
        data
      );
      setMessage(
        `Bulk: created ${res.data.created}, skipped ${res.data.skipped_duplicates}`
      );
      setBulkFile(null);
      loadProducts(filters);
    } catch (err) {
      setMessage(`Bulk error: ${err.response?.data?.error || err.message}`);
    }
  };

  // ─── JSX ────────────────────────────────────────────────────────────────────
  return (
    <div style={{ maxWidth: 800, margin: '1em auto' }}>
      {/* Logout */}
      <button
        onClick={() => {
          localStorage.removeItem('token');
          localStorage.removeItem('role');
          window.location.href = '/login';
        }}
        style={{ float: 'right', margin: '0.5em 0' }}
      >
        Logout
      </button>

      {/* Only admins see the Add/Edit form + bulk upload */}
      {isAdmin && (
        <>
          <h2>{editId ? 'Edit' : 'Add'} Product</h2>
          <form onSubmit={onSubmit} style={{ display: 'grid', gap: 8 }}>
            {[
              ['supplier', 'Supplier'],
              ['product_code', 'Product Code'],
              ['variant', 'Variant'],
              ['cost', 'Cost', 'number'],
              ['proposed_selling_price', 'Proposed Price', 'number'],
            ].map(([name, placeholder, type]) => (
              <input
                key={name}
                name={name}
                placeholder={placeholder}
                type={type || 'text'}
                value={form[name]}
                onChange={handleChange}
                required={['supplier', 'product_code', 'cost'].includes(name)}
              />
            ))}
            <textarea
              name="remark"
              placeholder="Remark"
              value={form.remark}
              onChange={handleChange}
            />
            <label>
              Images:
              <input
                type="file"
                accept="image/*"
                multiple
                onChange={handleImages}
              />
            </label>
            <div>
              <button type="submit">{editId ? 'Update' : 'Save'}</button>
              {editId && (
                <button
                  type="button"
                  onClick={resetForm}
                  style={{ marginLeft: 8 }}
                >
                  Cancel
                </button>
              )}
            </div>
          </form>

          <section style={{ margin: '2em 0' }}>
            <h3>Bulk Upload</h3>
            <form
              onSubmit={onBulkUpload}
              style={{ display: 'flex', gap: 8, alignItems: 'center' }}
            >
              <input
                type="file"
                accept=".xlsx"
                onChange={e => setBulkFile(e.target.files[0])}
              />
              <button type="submit">Upload Excel</button>
            </form>
          </section>
        </>
      )}

      {/* Any role sees the search bar + results */}
      <div style={{ margin: '1em 0', display: 'flex', gap: 8 }}>
        <input
          name="supplier"
          placeholder="Supplier"
          value={filters.supplier}
          onChange={e => setFilters({ ...filters, supplier: e.target.value })}
        />
        <input
          name="product_code"
          placeholder="Product Code"
          value={filters.product_code}
          onChange={e =>
            setFilters({ ...filters, product_code: e.target.value })
          }
        />
        <input
          name="variant"
          placeholder="Variant"
          value={filters.variant}
          onChange={e => setFilters({ ...filters, variant: e.target.value })}
        />
        <button onClick={() => loadProducts(filters)}>Search</button>
        <button
          onClick={() => {
            setFilters({ supplier: '', product_code: '', variant: '' });
            loadProducts({});
          }}
        >
          Clear
        </button>
      </div>

      {message && <p style={{ color: 'red' }}>{message}</p>}

      <hr style={{ margin: '2em 0' }} />

      <h2>All Products</h2>
      <table
        border="1"
        cellPadding="6"
        style={{ width: '100%', borderCollapse: 'collapse' }}
      >
        <thead>
          <tr>
            {[
              'ID',
              'Supplier',
              'Code',
              'Variant',
              ...(isAdmin ? ['Cost'] : []),
              'Remark',
              'Proposed',
              'Images',
              ...(isAdmin ? ['Actions'] : []),
              'Updated',
            ].map(h => (
              <th
                key={h}
                style={{
                  whiteSpace: 'normal',
                  wordWrap: 'break-word',
                  background: '#f0f0f0',
                }}
              >
                {h}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p.id}>
              <td>{p.id}</td>
              <td>{p.supplier}</td>
              <td>{p.product_code}</td>
              <td>{p.variant}</td>
              {isAdmin && <td>{p.cost}</td>}
              <td>{p.remark}</td>
              <td>{p.proposed_selling_price}</td>
              <td style={{ textAlign: 'center' }}>
                {p.image_urls && p.image_urls.length > 0
                  ? p.image_urls.map((url, i) => (
                      <a
                        key={i}
                        href={url}
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        <img
                          src={url}
                          alt={`img-${p.id}-${i}`}
                          style={{
                            width: 40,
                            height: 40,
                            objectFit: 'cover',
                            marginRight: 4,
                          }}
                        />
                      </a>
                    ))
                  : '—'}
              </td>
              {isAdmin && (
                <td>
                  <button onClick={() => onEdit(p)}>Edit</button>
                  <button
                    onClick={() => onDelete(p.id)}
                    style={{ marginLeft: 4 }}
                  >
                    Delete
                  </button>
                </td>
              )}
              <td>{new Date(p.date_updated).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
