// frontend/src/pages/AdminInput.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminInput.css'; // optional CSS file for styling

export default function AdminInput({ userRole }) {
  const [form, setForm] = useState({
    supplier: '',
    product_code: '',
    variant: '',
    cost: '',
    remark: '',
    proposed_selling_price: '',
  });
  const [products, setProducts] = useState([]);
  const [editId, setEditId] = useState(null);
  const [message, setMessage] = useState('');
  const [filters, setFilters] = useState({});

  // Ensure credentials are sent on every request
  axios.defaults.withCredentials = true;

  useEffect(() => {
    loadProducts(filters);
  }, [filters]);

  const loadProducts = async (filters = {}) => {
    try {
      const res = await axios.get('http://127.0.0.1:5000/api/admin/products', {
        params: filters,
      });
      setProducts(res.data);
    } catch (err) {
      console.error('GET error:', err.response || err);
      setMessage('Failed to load products');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = editId
        ? `http://127.0.0.1:5000/api/admin/products/${editId}`
        : 'http://127.0.0.1:5000/api/admin/products';
      const method = editId ? 'put' : 'post';
      await axios[method](url, form);
      setMessage(editId ? 'Product updated' : 'Product created');
      resetForm();
      loadProducts(filters);
    } catch (err) {
      console.error('SUBMIT error:', err.response || err);
      setMessage('Submit failed');
    }
  };

  const onEdit = (prod) => {
    setEditId(prod.id);
    setForm({
      supplier: prod.supplier,
      product_code: prod.product_code,
      variant: prod.variant,
      cost: prod.cost || '',
      remark: prod.remark || '',
      proposed_selling_price: prod.proposed_selling_price || '',
    });
  };

  const onDelete = async (id) => {
    if (!window.confirm('Delete this product?')) return;
    try {
      await axios.delete(
        `http://127.0.0.1:5000/api/admin/products/${id}`
      );
      setMessage(`Deleted id=${id}`);
    } catch (err) {
      console.error('DELETE error:', err.response || err);
      setMessage('Delete may have succeeded; refreshing list');
    } finally {
      loadProducts(filters);
      if (editId === id) resetForm();
    }
  };

  const resetForm = () => {
    setEditId(null);
    setForm({
      supplier: '',
      product_code: '',
      variant: '',
      cost: '',
      remark: '',
      proposed_selling_price: '',
    });
  };

  return (
    <div className="admin-input">
      <h1>ANZ Product Database</h1>

      {message && <div className="message">{message}</div>}

      <form onSubmit={handleSubmit}>
        <label>Supplier</label>
        <input
          name="supplier"
          value={form.supplier}
          onChange={(e) =>
            setForm({ ...form, supplier: e.target.value })
          }
          required
        />

        <label>Product Code</label>
        <input
          name="product_code"
          value={form.product_code}
          onChange={(e) =>
            setForm({ ...form, product_code: e.target.value })
          }
          required
        />

        <label>Variant</label>
        <input
          name="variant"
          value={form.variant}
          onChange={(e) =>
            setForm({ ...form, variant: e.target.value })
          }
          required
        />

        {userRole === 'admin' && (
          <>
            <label>Cost</label>
            <input
              name="cost"
              type="number"
              value={form.cost}
              onChange={(e) =>
                setForm({ ...form, cost: e.target.value })
              }
              required
            />

            <label>Proposed Selling Price</label>
            <input
              name="proposed_selling_price"
              type="number"
              value={form.proposed_selling_price}
              onChange={(e) =>
                setForm({
                  ...form,
                  proposed_selling_price: e.target.value,
                })
              }
              required
            />
          </>
        )}

        <button type="submit">
          {editId ? 'Update Product' : 'Add Product'}
        </button>
      </form>

      <h2>Product List</h2>
      <table>
        <thead>
          <tr>
            <th>Supplier</th>
            <th>Code</th>
            <th>Variant</th>
            {userRole === 'admin' && <th>Cost</th>}
            {userRole === 'admin' && <th>Price</th>}
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map((p) => (
            <tr key={p.id}>
              <td>{p.supplier}</td>
              <td>{p.product_code}</td>
              <td>{p.variant}</td>
              {userRole === 'admin' && <td>{p.cost}</td>}
              {userRole === 'admin' && (
                <td>{p.proposed_selling_price}</td>
              )}
              <td>
                <button onClick={() => onEdit(p)}>Edit</button>
                <button onClick={() => onDelete(p.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );  // ← closing the return
}      // ← closing the function
